import React , {useState} from 'react'
import { Link } from 'react-router-dom'
import "./header.css"
import Lo from "../../images/footgen-logo.png"

const Header = () => {
  const [isMobile,setIsMobile] = useState(false)
  const [isSearchOpen, setIsSearchOpen] = useState(false);

  const toggleSearch = () => {
    setIsSearchOpen(!isSearchOpen);
  };

  const handleSearch = (e) => {
    const searchTerm = e.target.value;
    console.log('Searching for:', searchTerm);
  };

  return (
    <div className='h'>
      <div className="h-left">
        <img src={Lo} alt="" className="logo" />
      </div>
      <div className="h-middle">
        <ul className={isMobile? "nav-links" : "navbar-list"}>

          <li className="navbar-item">
            <Link to="header" smooth={true} offset={200} duration={500} className="navbar-link">HOME</Link>
          </li>

          <li className="navbar-item">
            <Link to='main' smooth={true} offset={200} duration={500} className="navbar-link">PRODUCTS</Link>
          </li>

          <li className="navbar-item">
            <Link to='bestseller' smooth={true} offset={200} duration={500}className="navbar-link">SHOP</Link>
          </li>

          <li className="navbar-item">
            <Link to='sale' smooth={true} offset={200} duration={500}className="navbar-link">SALE</Link>
          </li>

          <li className="navbar-item">
            <Link to='footer' smooth={true} offset={200} duration={500} className="navbar-link">CONTACT</Link>
          </li>

        </ul>

      </div>
      <div className="h-right">
        <ion-icon onClick={toggleSearch} name="search-outline" aria-hidden="true" />
        {isSearchOpen && (
        <div className="search-bar">
          <input type="text" placeholder="Search..." onChange={handleSearch}/>
          {/* Add your search functionality here */}
        </div>
      )}
        <ion-icon name="person-outline" aria-hidden="true" />
        <ion-icon name="heart-outline" aria-hidden="true" />
        <ion-icon name="bag-outline" aria-hidden="true" />
      </div>

      <button className="nav-open-btn"
        onClick={() => setIsMobile(!isMobile)}
        >
            {isMobile? 
            (<ion-icon name="close" />):
            (<ion-icon name="menu-outline"/>)
            }
        </button>

    </div>
  )
}

export default Header
